signed main(){
  Int n;
  string s,t;
  cin>>n>>s>>t;
  {
    string x(s),y(t);
    x.erase(unique(x.begin(),x.end()),x.end());
    y.erase(unique(y.begin(),y.end()),y.end());
    if(x.size()>y.size()) swap(s,t);
  }
  const Int MAX = 5050;
  Int ans=1e9;

  using D = deque<Int>;
  auto gen=
    [&](string x){
      D res;
      char lst='$';
      for(char c:x){
        if(res.empty()||lst!=c) res.emplace_back(0);
        res.back()++;
        lst=c;
      }
      return res;
    };

  auto solve=
    [&]()->void{
      D v2=gen(t);

      for(Int i=0;i<MAX;i++){        
        D v1=gen(s);        
        Int res=0;

        Int posL=0,posR1=v1.size()-1,posR2=posR1;
        auto incL=
          [&](){
            chmin(posL,v1.size()-1);
            while(posL<(Int)v1.size()&&v1[posL]!=2) posL++;
            if(posL<(Int)v1.size()){
              v1[posL]=1;
              v1.emplace_front(1);
              res+=posL+1;
              posR1++;
              posR2++;
            }else{
              res+=posL;
            }   
          };

        auto incR=
          [&](){
            chmin(posR1,v1.size()-1);
            while(posR1>=0&&v1[posR1]!=2) posR1--;
            assert(posR1>=0);

            v1[posR1]=1;
            res+=v1.size()-posR1;
            v1.emplace_back(1);
          };
        auto decR=
          [&](){
            chmin(posR2,v1.size()-1);
            while(posR2>=0&&v1[posR2]!=1) posR2--;
            Int posR3=posR2-1;
            while(posR3>=0&&v1[posR3]!=1) posR3--;                        
            assert(posR3>=0);
            res+=v1.size()-(posR2+1);
            res+=v1.size()-(posR3+2);

            v1[posR2]=v1[posR3]=2;
            v1.pop_back();            
          };

        if(s[0]!=t[0]) incL();        
        for(Int j=0;j<i;j++){
          incL();incL();
        }

        while(v1.size()<v2.size()) incR();
        while(v1.size()>v2.size()) decR();

        Int a=0,b=0;
        for(Int i=0;i<(Int)v1.size();i++){
          a+=v1[i];
          b+=v2[i];
          res+=abs(a-b);
        }
        chmin(ans,res);
      }   
    };

  for(Int uku=0;uku<2;uku++){
    solve();
    reverse(s.begin(),s.end());
    reverse(t.begin(),t.end());
  }  
  cout<<ans<<endl;
  return 0;
}

