        int R1 = (cntt + L2 + R2) - (cnts + L1);
        if (R1 < 0) continue;
        if (((s[0] - '0') ^ (L1 & 1)) != ((t[0] - '0') ^ (L2 & 1))) {
          continue;
        }

        //printf("L1 = %d, L2 = %d, R1 = %d, R2 = %d\n", L1, L2, R1, R2);

        static int a[maxn];
        int cura = 0;
        for (int i = 0; i < L1; ++i) a[cura++] = 0;
        for (int i = 0; i < n - 1; ++i) {
          if (s[i] != s[i + 1]) a[cura++] = i;
        }
        for (int i = 0; i < R1; ++i) a[cura++] = n - 2;

        static int b[maxn];
        int curb = 0;
        for (int i = 0; i < L2; ++i) b[curb++] = 0;
        for (int i = 0; i < n - 1; ++i) {
          if (t[i] != t[i + 1]) b[curb++] = i;
        }
        for (int i = 0; i < R2; ++i) b[curb++] = n - 2;

        int sum = L1 + R1 + L2 + R2;
        for (int i = 0; i < cura; ++i) {
          sum += abs(a[i] - b[i]);
        }
        ans = min(ans, sum);

        //printf("Return %d\n", sum);
      }
    }
  }

  printf("%d\n", ans);
  return 0;
}

