		B.clear();

		for(int i = 1; i < n; ++i) if(a[i] == d) A.push_back(i);

		for(int i = 1; i <= n; ++i) B.push_back(0);
		for(int i = 1; i < n; ++i) if(b[i] == d) B.push_back(i);
		for(int i = 1; i <= n; ++i) B.push_back(n);

		int ans = INT_MAX;
		for(int i = 0; i + SZ(A) < SZ(B); ++i)
		{
