			for(int j = 0; j < i; ++j) res += B[j];
			for(int j = 0; j < SZ(A); ++j) res += abs(A[j] - B[i + j]);
			for(int j = i + SZ(A); j < SZ(B); ++j) res += abs(n - B[j]);
			chkmin(ans, res);
		}
		return ans;
	};

