int prea[MAXN], preb[MAXN];
void getmin(int & x, int y) { x > y ? x = y : 0; }

int dp[MAXN][MAXN];
int pre(int typ, int i) { return typ == 0 ? prea[i] : preb[i]; }
int suc(int typ, int i) {
	int res = typ == 0 ? prea[baka] - prea[i - 1] : preb[bakb] - preb[i - 1];
	res = (typ == 0 ? baka - i + 1 : bakb - i + 1) * n - res;
	return res;
}
int main() {
	std::ios_base::sync_with_stdio(false), std::cin.tie(0);
	std::cin >> n >> S + 1 >> T + 1;
	if (n <= 2) {
		int ans = 0;
		for (int i = 1; i <= n; ++i) ans += S[i] != T[i];
		std::cout << ans << std::endl;
		return 0;
	}
	for (int i = 1; i < n; ++i) {
		if (S[i] != S[i + 1]) {
			ax[++baka] = i;
			axt[baka] = S[i] == '1';
		}
		if (T[i] != T[i + 1]) {
			ay[++bakb] = i;
			ayt[bakb] = T[i] == '1';
		}
	}
	for (int i = 1; i <= baka; ++i) prea[i] = prea[i - 1] + ax[i];
	for (int i = 1; i <= bakb; ++i) preb[i] = preb[i - 1] + ay[i];
	int ans = std::numeric_limits<int>::max();
	memset(dp, 0x3f, sizeof dp);
	for (int i = 0; i <= baka; ++i)
		for (int j = 0; j <= bakb; ++j) {
			getmin(dp[i][j], pre(0, i) + pre(1, j));
			getmin(ans, dp[i][j] + suc(0, i + 1) + suc(1, j + 1));
			if (i < baka && j < bakb && axt[i + 1] == ayt[j + 1])
				getmin(dp[i + 1][j + 1], dp[i][j] + absx(ax[i + 1] - ay[j + 1]));
		}
	std::cout << ans << std::endl;
	return 0;
}

