using namespace std;

string S, T; vector<int>V1, V2; int N;

int solve() {
	int minx = (1 << 30);
	for (int i = 0; i <= N; i++) {
		if (i != S.size()) {
			if (S[i] != T[0] || (i >= 1 && S[i] == S[i - 1])) continue;
		}
		if (i == S.size()) {
			if (S[i - 1] == T[0]) continue;
		}
		int score = 0;
		for (int j = 0; j < i; j++) {
			if (S[j] != S[j + 1]) score += (j + 1);
		}
		int pos1 = lower_bound(V1.begin(), V1.end(), i) - V1.begin();
		int pos2 = lower_bound(V2.begin(), V2.end(), 0) - V2.begin();

		while (true) {
			pos1++; pos2++;
			if (pos1 == V1.size() || pos2 == V2.size()) break;
			score += abs(V1[pos1] - V2[pos2]);
		}
		for (int i = pos1; i < V1.size(); i++) score += abs((int)S.size() - V1[i]);
		for (int i = pos2; i < V2.size(); i++) score += abs((int)T.size() - V2[i]);
		minx = min(minx, score);
	}
	int S1 = 0;
	for (int i = 0; i < V1.size(); i++) S1 += abs(V1[i] - 0);
	for (int i = 0; i < V2.size(); i++) S1 += abs(N - V2[i]);
	minx = min(minx, S1);
	int S2 = 0;
	for (int i = 0; i < V1.size(); i++) S2 += abs(N - V1[i]);
	for (int i = 0; i < V2.size(); i++) S2 += abs(V2[i] - 0);
	minx = min(minx, S2);
	return minx;
}

int main() {
	cin >> N >> S >> T;
	V1.push_back(0); for (int i = 0; i < S.size() - 1; i++) { if (S[i] != S[i + 1]) V1.push_back(i + 1); } V1.push_back(S.size());
	V2.push_back(0); for (int i = 0; i < T.size() - 1; i++) { if (T[i] != T[i + 1]) V2.push_back(i + 1); } V2.push_back(T.size());

	int v1 = solve(); swap(S, T); swap(V1, V2);
	int v2 = solve(); swap(S, T); swap(V1, V2);
	cout << min(v1, v2) << endl;
	return 0;
}

