	vs[0] = s[0]-'0';
	vt[0] = t[0]-'0';
	repp(i,1,N){
		vs[i] = (s[i-1] != s[i]);
		vt[i] = (t[i-1] != t[i]);
	}
	vs[N] = s[N-1]-'0';
	vt[N] = t[N-1]-'0';
	vector<int> x,y;
	int c = 0;
	repp(i,0,N+1){
		if(vs[i] == 0){
			--c;
			x.push_back(i);
		}
		if(vt[i] == 0){
			++c;
			y.push_back(i);
		}
	}
	if(c < 0){
		swap(vs,vt);
		swap(x,y);
		c = -c;
	}
