			z.push_back(N);
		}
		sort(z.begin(),z.end());
		int d = 0;
		repp(i,0,z.size()) d += abs(y[i]-z[i]);
		ans = min(ans,d);
	}
	cout << ans+c/2 << endl;
	return 0;
}

