	int res=0;
	rep(i,1,k-1)
		res+=p[i];
	rep(i,1,B){
		if(k<=0)res+=q[i];
		else if(k<=A)res+=abs(p[k]-q[i]);
		else res+=n-q[i];
		k++;
	}
	if(k<=0)k=1;
	while(k<=A)res+=n-p[k++];
