const int MOD = 1000000007;
int inc(int a,int b) {
	return a + b >= MOD ? a + b - MOD : a + b;
}
int mul(int a,int b) {
	return 1LL * a * b % MOD;
}
int fpow(int x,int c) {
	int res = 1,t = x;
	while(c) {
		if(c & 1) res = mul(res,t);
		t = mul(t,t);
		c >>= 1;
	}
	return res;
}
int N,st[2],ed[2];
char s[5005],t[5005];
vector<int> a,b;
void Init() {
	read(N);
	scanf("%s%s",s + 1,t + 1);
	for(int i = 1 ; i <= 10010 ; ++i) {
		a.pb(0);b.pb(0);
	}
	if(s[1] == '0') a.pb(0);
	if(t[1] == '0') b.pb(0);
	st[0] = a.size() - 1;st[1] = b.size() - 1;
	for(int i = 1 ; i < N ; ++i) {
		if(s[i] != s[i + 1]) a.pb(i);
		if(t[i] != t[i + 1]) b.pb(i);
	}
	ed[1] = b.size();
	for(int i = 1 ; i <= 10010 ; ++i) {
		a.pb(N);b.pb(N);
	}
}
void Solve() {
	int p = st[0] - 2,q = ed[1] + 2;
	if((p & 1) != (q & 1)) ++q;
	int ans = N * N;
	while(q >= st[1] - N - 2) {
		int t0 = p + 1,t1 = q + 1;
		int res = abs(a[p] - b[q]);
		while(a[t0] != N || b[t1] != N) {
			res += abs(a[t0] - b[t1]);
			++t0;++t1;
		}
		t0 = p - 1,t1 = q - 1;
		while(a[t0] != 0 || b[t1] != 0) {
			res += abs(a[t0] - b[t1]);
			--t0;--t1;
		}
		ans = min(ans,res);
		q -= 2;
	}
	out(ans);enter;
}
