	{
		int ac = 0, bc = 0, ans = 0x3f3f3f3f;
		for (int i = 1; i < n; i++)
			if (S[i] == c && S[i + 1] != c)
				A[++ac] = i;
		for (int i = 1; i <= n; i++)
			B[++bc] = 0;
		for (int i = 1; i < n; i++)
			if (T[i] == c && T[i + 1] != c)
				B[++bc] = i;
		for (int i = 1; i <= n; i++)
			B[++bc] = n;
		for (int i = 1; i + ac - 1 <= bc; i++)
		{
			int v = 0;
			for (int j = 1; j <= ac; j++)
				v += abs(A[j] - B[i + j - 1]);
			for (int j = 1; j < i; j++)
				v += B[j];
			for (int j = i + ac; j <= bc; j++)
				v += n - B[j];
			ans = min(ans, v);
		}
		return ans;
	}

	void work()
	{
		scanf("%d%s%s", &n, S + 1, T + 1);
