int equal_ones(vi a, vi b)
{
	vi V[2];
	int c=0;
	for(int i=0;i<a.size();i++)
	{
		if(a[i]==1) c++;
		else V[0].pb(c);
	}
	c=0;
	for(int i=0;i<b.size();i++)
	{
		if(b[i]==1) c++;
		else V[1].pb(c);
	}
	assert(V[0].size()==V[1].size());
	int ans=0;
	for(int i=0;i<V[0].size();i++)
	{
		ans+=abs(V[0][i]-V[1][i]);
	}
	return ans;
}

int solve_absolute(vi a, vi b, int p1, int p2)
{
	int cnta,cntb; cnta=cntb=0;
	for(int i=0;i<a.size();i++) cnta+=(a[i]==2);
	for(int i=0;i<b.size();i++) cntb+=(b[i]==2);
	int ans = int(1e9);
	if(cnta>cntb)
	{
		int d = cnta-cntb;
		for(int i=0;i<=d;i++) //i L, d-i R
		{
			if((p1^(i&1))!=p2) continue;
			vi nw;
			int ct=0; int res=0; int l=0; int A = 0;
			for(int j=0;j<a.size();j++)
			{
				if(ct>=i) break;
				if(a[j]==2) 
				{
					A+=res+1; ct++;
				}
				res+=a[j]; l=j+1;
			}
			for(int i=0;i<res;i++) nw.pb(1);
			int r=int(a.size())-1;
			ct=0; res=0;
			for(int j=int(a.size())-1;j>=0;j--)
			{
				if(ct>=d-i) break;
				if(a[j]==2) 
				{
					A+=res+1; ct++;
				}
				res+=a[j]; r=j-1;
			}
			for(int j=l;j<=r;j++) nw.pb(a[j]);
			for(int j=0;j<res;j++) nw.pb(1);
			//cerr<<d<<' '<<i<<' '<<A<<' '<<equal_ones(nw,b)<<'\n';
			ans = min(ans, A+equal_ones(nw,b));
		}
	}
	else
	{
		int d = cntb-cnta;
		for(int i=0;i<=d;i++) //i L, d-i R
		{
			if((p1^(i&1))!=p2) continue;
			vi nw;
			int ct=0; int res=0; int l=0; int A = 0;
			for(int j=0;j<a.size();j++)
			{
				if(ct>=2*i) break;
				if(a[j]==2) 
				{
					res++;
				}
				else
				{
					A+=res; ct++;
					if(ct%2==0)
					{
						res++; A++;
					}
				}
				l=j+1;
			}
			for(int i=0;i<res;i++) nw.pb(2);
			int r=int(a.size())-1;
			ct=0; res=0;
			for(int j=int(a.size())-1;j>=0;j--)
			{
				if(ct>=2*(d-i)) break;
				if(a[j]==2) 
				{
					res++;
				}
				else
				{
					A+=res; ct++;
					if(ct%2==0)
					{
						res++; A++;
					}
				}
				r=j-1;
			}
			for(int j=l;j<=r;j++) nw.pb(a[j]);
			for(int j=0;j<res;j++) nw.pb(2);
			ans = min(ans, A+equal_ones(nw,b));
		}
	}
	return ans;
}

int solve(vi a, vi b, int p1, int p2)
{
	int ans = int(1e9);
	ans = min(ans, solve_absolute(a, b, p1, p2));
	//flip first 2
	for(int i=0;i<a.size();i++)
	{
		if(a[i]==2)
		{
			vi nw;
			int res = i+1;
			nw.pb(1); nw.pb(1);
			for(int j=0;j<i;j++) nw.pb(a[j]);
			for(int j=i+1;j<a.size();j++) nw.pb(a[j]);
			res += solve_absolute(nw, b, p1^1, p2);
			ans = min(ans, res);
			break;
		}
	}
	//flip first pair 1
	int ct=0; int res=0; int A=0;
	for(int i=0;i<a.size();i++)
	{
		if(a[i]==1)
		{
			ct++; A+=res;
			if(ct==2)
			{
				A++;
				vi nw; nw.pb(2);
				for(int j=0;j<i;j++)
				{
					if(a[j]==2) nw.pb(2);
				}
				for(int j=i+1;j<a.size();j++) nw.pb(a[j]);
				ans = min(ans, A+solve_absolute(nw,b,p1^1,p2));
				break;
			}
		}
		else res++;
	}
	return ans;
}

