{
	ios_base::sync_with_stdio(0); cin.tie(0);
	int n; cin>>n;
	string s,t;
	cin>>s>>t;
	int sum=1; vi a;
	for(int i=1;i<n;i++)
	{
		if(s[i]==s[i-1]) sum++;
		else {a.pb(sum); sum=1;}
	}
	a.pb(sum);
	sum=1; vi b;
	for(int i=1;i<n;i++)
	{
		if(t[i]==t[i-1]) sum++;
		else {b.pb(sum); sum=1;}
	}
	b.pb(sum);
	cout<<solve(a,b,s[0]-'0',t[0]-'0')<<'\n';
}

