vector<int> vec1, vec2;

inline int dis(int x, int y)
{
    int px, py;
    if(x<0) px=0;
    if(x>=(int)vec1.size()) px=n;
    if(x>=0 && x<(int)vec1.size()) px=vec1[x];
    if(y<0) py=0;
    if(y>=(int)vec2.size()) py=n;
    if(y>=0 && y<(int)vec2.size()) py=vec2[y];
    //printf("%d %d %d %d\n", x, y, px, py);
    return abs(px-py);
}

void solve()
{
    for(rint i=2; i<=n; ++i)
        if(s[i]=='0' && s[i-1]=='1') vec1.push_back(i-1);
    for(rint i=2; i<=n; ++i)
        if(t[i]=='0' && t[i-1]=='1') vec2.push_back(i-1);
    int r=vec2.size(), l=-r, temp=INF;
    for(rint i=l; i<=r; ++i)
    {
        int sum=0;
        for(rint j=0; j<min(i, (int)vec1.size()); ++j) sum+=vec1[j];
        for(rint j=0; j<vec2.size(); ++j) sum+=dis(i+j, j);
        for(rint j=i+vec2.size(); j<vec1.size(); ++j) sum+=n-vec1[j];
        //printf("%d %d!!!\n", i, sum);
        temp=min(temp, sum);
    }
    //printf("%d!!!\n", temp);
    ans+=temp;
}

