    if(n==2 && s[1]!=t[1] && s[1]==t[2] && s[1]==t[2]) {printf("2\n"); return 0;}
    solve();
    for(rint i=1; i<=n; ++i) s[i]=s[i]=='1'?'0':'1', t[i]=t[i]=='1'?'0':'1';
    vec1.clear(), vec2.clear();
    solve();
    printf("%d\n", ans);
    return 0;
}

