inline void doit() {
    for (int add0 = 0; add0 <= 2 * n + 1; add0++) {
        vector <p> na;
        if (add0 <= n) {
            na = vector <p> (add0, p('0', 1));
            for (p v : a) na.pb(v);
        } else {
            na = vector <p> (add0 - n - 1, p('1', 1));
            for (p v : a) na.pb(v);
        }
        int i = 0, j = 0;
        bool valid = true;
        int now = 0;
        while (!(i == na.size() && j == b.size()) && valid) {
            if (i == na.size()) now += n + 1 - b[j++].second;
            else if (j == b.size()) now += n + 1 - na[i++].second;
            else {
                valid &= na[i].first == b[j].first;
                now += abs(na[i++].second - b[j++].second);
            }
        }
        if (valid) 
            ans = min(ans, now);
    }
}

