using namespace std;
const int N=5005;
int n,ans;
vector<P>S,T;
char s[N],t[N];
void work(vector<P>&a,vector<P>&b){
	int pre=0,s;
	fo(i,0,a.size()-1){
		if(a[i].y==b[0].y){
			s=pre;
			int j=i,k=0;
			for(;j<a.size()&&k<b.size();++j,++k)
				s+=abs(a[j].x-b[k].x);
			for(;j<a.size();++j)s+=n-a[j].x;
			for(;k<b.size();++k)s+=n-b[k].x;
			ans=min(ans,s);
		}
		pre+=a[i].x;
	}
	for(P i:b)pre+=n-i.x;
	ans=min(ans,pre);
}
int main(){
	scanf("%d\n%s\n%s",&n,s+1,t+1);
	fo(i,1,n-1){
		if(s[i]!=s[i+1])S.pb(P(i,s[i]-'0'));
		if(t[i]!=t[i+1])T.pb(P(i,t[i]-'0'));
	}
	ans=1e9;
	work(S,T);
	work(T,S);
	cout<<ans;
}

