vector<pair<int, int>> get_lines(string s) {
  vector<pair<int, int>> ans;
  ans.push_back(s[0] == '0' ? make_pair(0, 0) : make_pair(1, 0));
  for (int i = 1; i < n; ++i)
    if (s[i] != s[i - 1])
      ans.push_back({ans.back().first ^ 1, i});
  ans.push_back({ans.back().first ^ 1, n});
  return ans;
}

int32_t main() {
  ios::sync_with_stdio(false);
  cin.tie(0);
  cin >> n >> s >> t;
  vector<pair<int, int>> a = get_lines(s);
  vector<pair<int, int>> b = get_lines(t);
  reverse(b.begin(), b.end());
  for (int i = 0; i < 5005; ++i)
    b.push_back({b.back().first ^ 1, 0});
  reverse(b.begin(), b.end());
  for (int i = 0; i < 5005; ++i)
    b.push_back({b.back().first ^ 1, n});
  int ans = 2e9;
  for (int i = 0; i < b.size(); ++i) {
    if (b[i].first == a[0].first) {
      int x = 0, y = i;
      int cur = 0;
      while (y >= 0) {
        int u = 0, v = b[y].second;
        cur += v - u;
        --y;
      }
      x = 1, y = i + 1;
      while (y < b.size()) {
        int u = (x < a.size() ? a[x].second : n);
        int v = b[y].second;
        cur += abs(v - u);
        ++x;
        ++y;
      }
      ans = min(ans, cur);
    }
  }
  cout << ans << '\n';
  return 0;
