	for(int k = 0; k < 2; k++) {
		for(int i = 1; i < L; i++)
			if(S[k][i] == S[k][i-1]) pos0[k].push_back(i-1);
		N[k] = pos0[k].size();
	}
	if(N[0]+N[1] == 0) {
		if(S[0][0] == S[1][0]) cout << "0\n";
