	for(int k = 0; k < 2; k++) {
		id_gt[k].resize(N[k]);
		int a = 0;
		for(int i = 0; i < N[k]; i++) {
			while(a < N[1-k] && pos0[1-k][a] < pos0[k][i]) a++;
			if(k == 1) while(a < N[1-k] && pos0[1-k][a] <= pos0[k][i]) a++;
