		add[N[0]+i+1] -= pos0[1][i];
		// cost[d] -= B[i]: A[i-d] > B[i]
		add[N[0]+i-N[0]+1] -= pos0[1][i];
		add[N[0]+i-id_gt[1][i]+1] += pos0[1][i];
	}
	for(int i = 0; i < N[0]; i++) {
		add[N[0]+N[1]-i] += L-1-pos0[0][i];
		// add[N[0]+N[1]+1] -= L-1-pos0[0][i];
		add[0] += 1+pos0[0][i];
		add[N[0]-i] -= 1+pos0[0][i];
	}
	for(int i = 0; i < N[1]; i++) {
		add[N[0]+i+1] += 1+pos0[1][i];
		// add[N[0]+N[1]+1] -= 1+pos0[1][i];
		add[0] += L-1-pos0[1][i];
		add[i+1] -= L-1-pos0[1][i];
	}
	cat a = 0;
	for(int i = 0; i <= N[0]+N[1]; i++) {
		a += add[i];
		cost[i] += a;
	}
	int dif = (S[0][0] != S[1][0]) ^ (N[0]&1);
	cat ans = cost[dif];
	for(int i = dif; i <= N[0]+N[1]; i += 2) ans = min(ans, cost[i]);
	cout << ans << "\n";
	return 0;
}

