		for(int j=i+a;j<=b;++j)res+=std::min(B[j],n-B[j]);
		for(int j=c-i+2;j<=a;++j)res+=std::min(A[j],n-A[j]);
		ans=std::min(res,ans);
	}
	for(int i=1;i<=a;i+=2){
		int res=0,c=std::min(a,b+i-1);
		for(int j=1;j<i;++j)res+=A[j];
		for(int j=i;j<=c;++j)res+=abs(B[j-i+1]-A[j]);
		for(int j=i+b;j<=a;++j)res+=std::min(A[j],n-A[j]);
		for(int j=c-i+2;j<=b;++j)res+=std::min(B[j],n-B[j]);
		ans=std::min(res,ans);
	}
	return printf("%d\n",ans),0;
}

