	for (int i=1;i<N;i++) if (S[i-1] == S[i]) s.push_back(i);
	for (int i=1;i<N;i++) if (T[i-1] == T[i]) t.push_back(i);

	long long ans = 1e18;
	for (int mk=-N;mk<=N;mk++) if ((S[0] - '0' + mk % 2 + 2 + T[0] - '0') % 2 == 0){
		auto p = s, q = t;
		if (mk < 0) for (int k=0;k<-mk;k++) p.push_front(-1);
		if (mk > 0) for (int k=0;k<mk;k++) q.push_front(-1);
		while (p.size() < q.size()) p.push_back(-2);
		while (q.size() < p.size()) q.push_back(-2);

		long long sum = 0;
		for (int i=0;i<p.size();i++){
			if (p[i] == -1 && q[i] == -1);
			else if (p[i] == -2 && q[i] == -2);
			else if (p[i] == -1 && q[i] == -2) sum += N;
			else if (p[i] == -2 && q[i] == -1) sum += N;
			else if (p[i] == -1) sum += q[i];
			else if (q[i] == -1) sum += p[i];
			else if (p[i] == -2) sum += N - q[i];
			else if (q[i] == -2) sum += N - p[i];
			else sum += abs(p[i] - q[i]);
		}

		if (ans > sum)
			ans = sum;
	}

	printf ("%lld\n",ans);

	return 0;
}

