			int tmp = i - ((int)b.size() - 1 - j);
			if(tmp >= 0){
				now += (abs(a[tmp].SE - b[j].SE));
			}
			else{
				now += b[j].SE;
			}
		}
		ret = min(ret, now);
	}
	return ret;
}

int main(){

	int n;cin >> n;
	string s,t;cin >> s >> t;
	auto a = make_seica(s);
	auto b = make_seica(t);

	cout << min(solve_seica(a,b),solve_seica(b,a)) << endl;

	return 0;
}

