vector<int> get_seq(char *s)
{
	vector<int> ret;
	for(int i=1; i<=n+1; i++)
		if(s[i] != s[i-1])
			ret.push_back(i);
	return ret;
}

int calc(vector<int> a, vector<int> b)
{
	int ret = 0;
	for(int i=1; i<=b.size(); i++)
		if(i > a.size())
			ret += abs(b[b.size()-i]-1);
		else ret += abs(b[b.size()-i]-a[a.size()-i]);
	return ret;
}

void work()
{
	int ans = 1e9;
	vector<int> s0 = get_seq(str), t0 = get_seq(tar), s, t;
	for(int i=(str[n]!=tar[n]); i<=s0.size(); i+=2)
	{
		s = s0, t = t0;
		for(int j=1; j<=i; j++) s.push_back(n+1);
		ans = min(calc(s, t), ans);

		s = s0, t = t0;
		for(int j=1; j<=i; j++) s.pop_back();
		ans = min(calc(s, t), ans);
	}
	printf("%d\n", ans);
}

