    b.insert(b.begin(), 0); b.insert(b.begin(), 0);
    ans = min(ans, dist(a, b));
  }
  b = tmpb; a = tmpa;
  for (int rep = 0; rep < x; ++rep) b.push_back(n);
  for (int rep = 2; rep <= n; ++rep) {
    b.push_back(n); b.push_back(n);
    a.insert(a.begin(), 0); a.insert(a.begin(), 0);
    ans = min(ans, dist(a, b));
  }
  b = tmpb; a = tmpa;
  if (x & 1) {
    ++x;
    a.push_back(n);
  }
  for (int rep = 0; rep < x; ++rep) b.insert(b.begin(), 0);
  for (int rep = 2; rep <= n; ++rep) {
    b.insert(b.begin(), 0); b.insert(b.begin(), 0);
    a.push_back(n); a.push_back(n);
  }
  return ans;
}

