inline void init_string( pii *a, string str, int &len )
{
	len = 0; int last = str[0] - '0'; 

	a[++len] = make_pair( last ^ 1, 0 ); 

	for( int i = 2; i <= n; i ++ )
	{
		if( last == str[i-1] - '0' ) continue;

		last ^= 1; 
		a[++len] = make_pair( last ^ 1, i - 1 ); 
	}

	a[++len] = make_pair( last, n ); 
} 

inline int calc_ans( int st )
{
	int ret = 0;

	// clear 1 -> st
	for( int i = 1; i < st; i ++ )
		ret += s[i].second; 

	for( int i = 1; i <= lenT; i ++ )
	{
		if( st < 1 ) {
			ret += t[i].second; st ++; continue;
		}

		if( st > lenS ) {
			ret += n - t[i].second; st ++; continue;
		}

		if( t[i].first != s[st].first ) return INF;
		ret += abs( t[i].second - s[st].second ); 		
		st ++; 
	}

	// clear st -> n 
	for( int i = st; i <= lenS; i ++ )
		ret += n - s[i].second; 

	return ret;
}

