{
//	freopen( "e.in", "r", stdin ); 

	cin >> n >> strS >> strT; 

	init_string( s, strS, lenS );
	init_string( t, strT, lenT );  

	int ans = INF;
	for( int i = 1 - lenT; i <= lenS; i ++ )
		ans = min( ans, calc_ans( i ) ) ;

	cout << ans << endl;

	return 0; 
}

