int solve(int t1, int t2)
{
	int sum = 0;
	for (int i = 1; i <= t1 - 1; i++)
		sum += pos[0][i];
	for (int i = 1; i <= t2 - 1; i++)
		sum += pos[1][i];
	while (t1 <= cnt[0] || t2 <= cnt[1])
	{
		int p1, p2;
		if (t1 > cnt[0]) p1 = n;
		else p1 = pos[0][t1];
		if (t2 > cnt[1]) p2 = n;
		else p2 = pos[1][t2];
		sum += abs(p1 - p2);
		t1++; t2++;
	}
	return sum;
}
