			pos[i][++cnt[i]] = 0;
		for (int j = 1; j <= n - 1; j++)
			if (s[j] != s[j + 1]) pos[i][++cnt[i]] = j;
	}
	int res = INF;
	for (int i = 1; i <= cnt[2]; i += 2)
		res = min(res, solve(1, i));
	for (int i = 1; i <= cnt[1]; i += 2)
		res = min(res, solve(1, i));
	printf("%d\n", res);
	return 0;
}

