	}
	factinv[max_n - 1] = modint(1) / fact[max_n - 1];
	for (int i = max_n - 2; i >= 0; i--) {
		factinv[i] = factinv[i + 1] * modint(i + 1);
	}
}
modint comb(int a, int b) {
	if (a < 0 || b < 0 || a < b)return 0;
	return fact[a] * factinv[b] * factinv[a - b];
}


vector<int> trans(string s) {
	vector<int> res;
	rep(i, s.size()) {
		if (i + 1 < s.size() && s[i] == s[i + 1]) {
			res.push_back(2); i++;
		}
		else res.push_back(1);
	}
	return res;
}

int calc(string s, string t) {
	vector<int> a = trans(s);
	vector<int> b = trans(t);
	int c1 = 0;
	rep(i, a.size())if (a[i] == 1)c1++;

	int res = mod;
	for (int i = 0; 2 * i <= c1; i++) {
		int cx = (s[0] == '1');
		int cy = (t[0] == '1');
		if (i % 2)cx ^= 1;
		if (cx != cy)continue;

		int sum = 0;
		int tmp = 0;
		vector<int> v;
		rep(j, a.size()) {
			if (a[j] == 1) {
				if (tmp < 2 * i) {
					sum += j - tmp;
					tmp++;
				}
				else {
					v.push_back(1);
				}
			}
			else {
				v.push_back(2);
			}
		}
		for (int j = i-1; j >= 0; j--) {
			sum += 1 + 2 * j;
		}

		vector<int>w(i, 2);
		for (int ad : v)w.push_back(ad);

		if (w.size() < b.size()) {
			int dif = b.size() - w.size();
			vector<int> nw;

			int x = 0;
			per(i, (int)w.size()) {
				if (w[i] == 1) {
					x++;
					nw.push_back(1);
				}
				else {
					if (dif > 0) {
						dif--;
						sum += x;
					}
					else {
						nw.push_back(2);
					}
				}
			}
			reverse(all(nw));
			dif = b.size() - w.size();
			rep(j, dif) {
				nw.push_back(1);
				nw.push_back(1);
				sum += 1 + 2 * (dif - j - 1);
			}
			swap(nw, w);
		}
		if (w.size() > b.size()) {
			int dif = w.size() - b.size();
			vector<int> nw;
			int x = 0;

			int rest = 2 * dif;
			per(j, (int)w.size()) {
				if (w[j] == 1) {
					if (rest > 0) {
						sum += x; rest--;
					}
					else {
						nw.push_back(1);
					}
				}
				else {
					nw.push_back(2);
					x++;
				}
			}
			reverse(all(nw));
			rep(j, dif) {
				nw.push_back(2);
				sum += 1 + 2 * (dif - 1 - j);
			}
			swap(nw, w);
		}
		vector<int> a1, b1;
		rep(i, w.size())if (w[i] == 2)a1.push_back(i);
		rep(i, b.size())if (b[i] == 2)b1.push_back(i);
		rep(i, a1.size()) {
			sum += abs(a1[i] - b1[i]);
		}
		//cout << s << " " <<t<<" "<< i << " " << sum << "\n";
		res = min(res, sum);
	}
	return res;

}
void solve() {
	int n; cin >> n;
	string s, t; cin >> s >> t;
	if (n <= 2) {
		int ans = 0;
		rep(i, n)if(s[i] != t[i])ans++;
		cout << ans << "\n"; return;
	}
	if (n <= 3) {
		int ans = 0;
		if (s == t) {
			cout << ans << "\n"; return;
		}
		map<string, bool> used;
		vector<string> cur; cur.push_back(s);
		used[s] = true;

		auto valid = [&](string s)->bool {
			rep1(i, n - 1)if (s[i - 1] == s[i] && s[i] == s[i + 1])return false;
			return true;
		};
		while (true) {
			ans++;
			vector<string> nex;
			for (string a : cur) {
				rep(i, n) {
					string cop = a;
					cop[i] = '0' + '1' - cop[i];
					if (valid(cop) && !used[cop]) {
						nex.push_back(cop);
						used[cop] = true;
					}
				}
			}
			swap(cur, nex);
			for (string a : cur)if (a == t) {
				cout << ans << "\n"; return;
			}
		}
	}

	if (trans(s).size() < trans(t).size()) {
		swap(s, t);
	}
	int ans = calc(s, t);
	reverse(all(s)); reverse(all(t));
	ans = min(ans, calc(s, t));
	cout << ans << "\n";
}




