int solve(vector <int> a, vector <int> b) {
  if(a.size() < b.size()) swap(a, b);
  int ans = 2e9;
  for(int bef = 0; bef <= a.size(); ++ bef) {
    int res = 0;
    vector <int> A, B; A.clear(); B.clear();
    for(int j = 1; j <= bef; ++ j) B.push_back(0);
    for(int j = 0; j < (int) b.size(); ++ j) B.push_back(b[j]);
    A = a;
    while(A.size() < B.size()) A.push_back(n);
    while(B.size() < A.size()) B.push_back(n);
    for(int j = 0; j < (int) A.size(); ++ j)
      res += abs(A[j] - B[j]);
    ans = min(ans, res);
  }

  for(int bef = 0; bef <= a.size(); ++ bef) {
    int res = 0;
    vector <int> A, B; A.clear(); B.clear();
    for(int j = 1; j <= bef; ++ j) A.push_back(0);
    for(int j = 0; j < (int) a.size(); ++ j) A.push_back(a[j]);
    B = b;
    while(A.size() < B.size()) A.push_back(n);
    while(B.size() < A.size()) B.push_back(n);
    for(int j = 0; j < (int) A.size(); ++ j)
      res += abs(A[j] - B[j]);
    ans = min(ans, res);
  }

  return ans;
}

