	xa[0] = xa[1] = xb[0] = xb[1] = -1;
	for (int i = n; i; --i) {
		if (a[i] != -1) xa[a[i]] = i;
		if (b[i] != -1) xb[b[i]] = i;
	}
	for (int i = 1; i < n; ++i) {
		if (a[i] != -1) calc(i, xb[a[i]]);
		if (b[i] != -1) calc(xa[b[i]], i);
	}
	printf("%d", ans);
	return 0;
}

