vector<pair<int, int>> get_lines(string s) {
  vector<pair<int, int>> ans;
  ans.push_back(s[0] == '0' ? make_pair(0, 0) : make_pair(1, 0));
  for (int i = 1; i < n; ++i)
    if (s[i] != s[i - 1])
      ans.push_back({ans.back().first ^ 1, i});
  ans.push_back({ans.back().first ^ 1, n});
  return ans;
}

