const int maxn = 5000;
const int inf = ~0U >> 2;

int n;
char s[maxn + 1], t[maxn + 1];

int main(void) {
  scanf("%d", &n);
  scanf("%s%s", s, t);
  int cnts = 0, cntt = 0;
  for (int i = 0; i < n - 1; ++i) {
    if (s[i] != s[i + 1]) ++cnts;
    if (t[i] != t[i + 1]) ++cntt;
  }
  if (cnts > cntt) {
    for (int i = 0; i < n; ++i) {
      swap(s[i], t[i]);
    }
    swap(cnts, cntt);
  }

  if (n == 1) {
    puts(s[0] == t[0] ? "0" : "1");
    return 0;
  }

  int ans = inf;
  for (int L1 = 0; L1 <= cntt - cnts; ++L1) {
    for (int L2 = 0; L2 <= 1; ++L2) {
      for (int R2 = 0; R2 <= 1; ++R2) {
        int R1 = (cntt + L2 + R2) - (cnts + L1);
        if (R1 < 0) continue;
        if (((s[0] - '0') ^ (L1 & 1)) != ((t[0] - '0') ^ (L2 & 1))) {
          continue;
        }

        static int a[maxn];
        int cura = 0;
        for (int i = 0; i < L1; ++i) a[cura++] = 0;
        for (int i = 0; i < n - 1; ++i) {
          if (s[i] != s[i + 1]) a[cura++] = i;
        }
        for (int i = 0; i < R1; ++i) a[cura++] = n - 2;

        static int b[maxn];
        int curb = 0;
        for (int i = 0; i < L2; ++i) b[curb++] = 0;
        for (int i = 0; i < n - 1; ++i) {
          if (t[i] != t[i + 1]) b[curb++] = i;
        }
        for (int i = 0; i < R2; ++i) b[curb++] = n - 2;

        int sum = L1 + R1 + L2 + R2;
        for (int i = 0; i < cura; ++i) {
          sum += abs(a[i] - b[i]);
        }
        ans = min(ans, sum);
      }
    }
  }

  printf("%d\n", ans);
  return 0;
}

