const int maxn=5e3+20;
const int inf=0x3f3f3f3f;

#define pii pair<int,int>

int n;
vector<pii >s;
vector<pii >t;
char str[maxn];

inline void init()
{
	n=read();
	scanf("%s",str+1);
	s.push_back(mkr(0,str[1]=='0'));
	REP(i,1,n-1)if(str[i]!=str[i+1])s.push_back(mkr(i,str[i]=='1'));
	s.push_back(mkr(n,str[n]=='1'));
	scanf("%s",str+1);
	t.push_back(mkr(0,str[1]=='0'));
	REP(i,1,n-1)if(str[i]!=str[i+1])t.push_back(mkr(i,str[i]=='1'));
	t.push_back(mkr(n,str[n]=='1'));
	if(SZ(s)>SZ(t))swap(s,t);
}

inline void doing()
{
	vector<pii >Top; Top.push_back(s.front());
	REP(i,1,n)Top.push_back(mkr(0,Top.back().se^1));
	reverse(Top.begin(),Top.end()); Top.pop_back();
	Top.insert(Top.end(),s.begin(),s.end());
	REP(i,1,n)Top.push_back(mkr(n,Top.back().se^1));

	int ans=inf;

	REP(i,0,SZ(Top)-SZ(t))if(Top[i].se==t[0].se)
	{
		int sum=0;
		REP(j,0,i-1)sum+=Top[j].fi;
		REP(j,i+SZ(t),SZ(Top)-1)sum+=n-Top[j].fi;
		REP(j,0,SZ(t)-1)
			sum+=abs(Top[i+j].fi-t[j].fi);
		chkmin(ans,sum);
	}
	printf("%d\n",ans);
}

