vector<pair<int,int>> make_seica(string str){
	vector<pair<int,int>> ret;
	if(str[0] == '0')ret.EB(1,0);
	else ret.EB(0,0);
	REP(i,str.size()-1){
		if(str[i] != str[i+1])ret.EB(str[i] - '0',i+1);
	}
	if(str[str.size() - 1] == '0')ret.EB(0,str.size());
	else ret.EB(1,str.size());

	while(ret.size() < 5005)ret.EB(1-ret.back().FI,str.size());

	return ret;
}

int solve_seica(vector<pair<int,int>> a, vector<pair<int,int>> b){
	int ret = INF;
	REP(i,a.size()){
		int now = 0;
		if(a[i].FI != b[b.size() - 1].FI)continue;
		REV(j,b.size()){
			int tmp = i - ((int)b.size() - 1 - j);
		//	cout << tmp << " " << j << endl;
			if(tmp >= 0){
				now += (abs(a[tmp].SE - b[j].SE));
			}
			else{
				now += b[j].SE;
			}
		}
		//cout << "      " << i << " " << now << endl;
		ret = min(ret, now);
	}
	return ret;
}

