void fast_print(__int128 x) {
	if (x == 0) { fast_print('0'); return; }
	if (x < 0) {
		fast_print('-');
		x = -x;
	}
	__int128 p = 1;
	while (x / (p * 10)) p *= 10;
	while (p) {
		__int128 symb = x / p;
		fast_print((int)symb);
		x -= p * symb;
		p /= 10;
	}
};
