vector<pii> groups(string s) {
	char pref = '!';
	int cnt = 0;
	int start = -1;
	vector<pii> g;
	for (int i = 0; i <= len(s); i++)
		if (s[i] == pref)
			cnt++;
		else {
			if (pref == '1') {
				g.emplace_back(start, i - 1);
			}
			start = i;
			pref = s[i];
			cnt = 1;
		}
	return g;
}

