	for (int i = 0; i <= len(b); i++) {
		ll sum = offset;
		if (i < len(b))
			offset += b[i].x + 1 + b[i].y;
		if (len(b) - i > len(a)) continue;
		for (int j = 0; j < len(a); j++) {
			pii target = ((i + j < len(b)) ? b[i + j] : mp(len(s), len(s)));
			sum += abs(a[j].x - target.x) + abs(a[j].y - target.y);
		}
		rez = min(rez, sum);
	}
