int n;

int dist(const vector<int>& a, const vector<int>& b) {
  int ret = 0;
  for (int i = 0; i < a.size(); ++i) ret += abs(a[i] - b[i]);
  return ret;
}

int calc(vector<int> a, vector<int> b) {
  if (a.size() < b.size()) swap(a, b);
  int ans = 1 << 30;
  int x = a.size() - b.size();
  vector<int> tmpb = b, tmpa = a;
  for (int rep = 0; rep < x; ++rep) b.push_back(n);
  ans = dist(a, b);
  for (int rep = 2; rep <= x; rep += 2) {
    b.pop_back(); b.pop_back();
    b.insert(b.begin(), 0); b.insert(b.begin(), 0);
    ans = min(ans, dist(a, b));
  }
  b = tmpb; a = tmpa;
  for (int rep = 0; rep < x; ++rep) b.push_back(n);
  ans = min(ans, dist(a, b));
  for (int rep = 2; rep <= n; ++rep) {
    b.push_back(n); b.push_back(n);
    a.insert(a.begin(), 0); a.insert(a.begin(), 0);
    ans = min(ans, dist(a, b));
  }
  b = tmpb; a = tmpa;
  if (x & 1) {
    ++x;
    a.push_back(n);
  }
  for (int rep = 0; rep < x; ++rep) b.insert(b.begin(), 0);
  ans = min(ans, dist(a, b));
  for (int rep = 2; rep <= n; ++rep) {
    b.insert(b.begin(), 0); b.insert(b.begin(), 0);
    a.push_back(n); a.push_back(n);
    ans = min(ans, dist(a, b));
  }
  return ans;
}

