using namespace std;

const int MAXN = 5005;

int n,tota,totb,ans;
int a[MAXN];
int b[MAXN];

string s,t;

int main()
{
	ios::sync_with_stdio(0);
	cin >> n >> s >> t;
	for (int i = 0;i < n - 1;i++)
		if (s[i] == s[i + 1])
			a[++tota] = i + 1;
	for (int i = 0;i < n - 1;i++)
		if (t[i] == t[i + 1])
			b[++totb] = i + 1;
	if (tota > totb)
	{
		swap(tota,totb);
		swap(a,b);
	}
	ans = 1e9;
	for (int st = -tota;st <= totb + 2;st++)
	{
		if ((~st & 1) != (s[0] != t[0]))
			continue;
		int l = min(1,st),r = max(st + tota - 1,totb),res = 0;
		for (int i = l;i <= r;i++)
		{
			int A = 0,B = 0;
			if (i >= st && i <= st + tota - 1)
				A = a[i - st + 1];
			else if (i > st + tota - 1)
				A = n;
			if (i >= 1 && i <= totb)
				B = b[i];
			else if (i > totb)
				B = n;
			res += abs(A - B);
		}
		ans = min(ans,res);
	}
	cout << ans << endl;
	return 0;
}

