int main() {
    scanf("%d", &n);
    for(int i=1;i<=n;++i) {
        scanf("%1d", &a[i]);
        num1+=(i==1 || a[i]!=a[i-1]) ? 1 : 0;
    }
    for(int i=1;i<=n;++i) {
        scanf("%1d", &b[i]);
        num2+=(i==1 || b[i]!=b[i-1]) ? 1 : 0;
    }
    if(num1>num2) {
        for(int i=1;i<=n;++i) {
            swap(a[i], b[i]);
        }
        swap(num1, num2);
    }
    for(int i=1;i<=n;++i) {
        g[++m]=2;
    }
    for(int i=1;i<=n;++i) {
        if(i==1 || b[i]!=b[i-1]) g[++m]=2;
        g[++m]=b[i];
    }
    for(int i=0;i<=n;++i) {
        g[++m]=2;
    }
    ans=1e9;
    for(int i=1;i+num1+n<=m;++i) {
        if(a[1]==b[1] && !((n+i)&1)) continue;
        else if(a[1]!=b[1] && ((n+i)&1)) continue;
        for(int j=1;j<=i;++j) {
            f[j]=2;
        }
        int now=i;
        for(int j=1;j<=n;++j) {
            if(j>1 && a[j]!=a[j-1]) f[++now]=2;
            f[++now]=a[j];
        }
        for(;++now<=m;) {
            f[now]=2;
        }
        int L=1, R=1, res=0;
        // for(int j=1;j<=m;++j) {
        //  printf("%c", f[j]<=1 ? f[j]+'0' : '|');
        // } puts("");
        // for(int j=1;j<=m;++j) {
        //  printf("%c", g[j]<=1 ? g[j]+'0' : '|');
        // } puts("");
        while(L<=m && R<=m) {
            if(f[L]==2 && g[R]==2) {
                res+=abs(L-R);
                ++L;
                ++R;
            }
            while(L<=m && f[L]!=2) ++L;
            while(R<=m && g[R]!=2) ++R;
        }
        // printf("==> %d\n", res);
        ans=min(ans, res);
    }
    printf("%d\n", ans);
    return 0;
}

