	for (int i = 1; i < n; ++i) if (t[i] != t[i - 1]) T.emplace_back(i);

	if (S.size() < T.size()) std::swap(S, T);
	while (T.size() < S.size()) T.emplace_back(n);

	for (int i = 0; i < 4; ++i) S.insert(S.begin(), 0), T.insert(T.begin(), 0);
	for (int i = 0; i < 4; ++i) S.emplace_back(n), T.emplace_back(n);

