const int C = 5556;
vector<ii> getlines(string s, bool ext=1)
{
	vector<ii> vec; int n=s.length();
	for(int i=0;i<C;i++)
	{
		if(i<C-1&&!ext) continue;
		vec.pb(mp(s[0]-'0',0));
		if(i%2==0) vec.back().fi^=1;
	}
	for(int i=0;i+1<n;i++)
	{
		if(s[i]!=s[i+1])
		{
			vec.pb(mp(s[i+1]-'0',i+1));
		}
	}
	for(int i=0;i<C;i++)
	{
		if(i>0&&!ext) break;
		vec.pb(mp(s[n-1]-'0',n));
		if(i%2==0) vec.back().fi^=1;
	}
	return vec;
}
