			{
				res+=abs(b[j].se-a[i+j].se);
				//cerr<<b[j].se<<' '<<a[i+j].se<<' '<<abs(b[j].se-a[i+j].se)<<'\n';
			}
			if(a[i+int(b.size())-1].se<n)
