vector<ll> x,y;
ll n;
int main(){
    string s,t;cin>>n>>s>>t;
    if(n==1)return NOEL(s!=t);
    if(n==2)return NOEL((s[0]!=t[0])+(s[1]!=t[1]));
    y.push_back(0);
    for(int i=1;i<n;i++){
      if(s[i]!=s[i-1])x.push_back(i);
      if(t[i]!=t[i-1])y.push_back(i);
    }
    y.push_back(n);
    ll ans=1e17;
    for(int k=-N;k<N;k++){
      if(k%2==0 xor t[y[0]]==s[x[0]])continue;
      ll cur=0;
      for(int i=0;i<y.size();i++){
	int j=i+k; ll z;
	if(j<0)z=0;
	else if(j>=x.size())z=n;
	else z=x[j];
	cur+=abs(y[i]-z);
      }
      ans=min(ans,cur);
    }
    cout<<ans<<endl;
    return 0;
}

