  int n;
  string s, t;
  cin >> n >> s >> t;
  vector<int> a, b;
  for (int i = 1; i < n; ++i) if (s[i] != s[i - 1]) a.push_back(i);
  for (int i = 1; i < n; ++i) if (t[i] != t[i - 1]) b.push_back(i);
  if (a.size() < b.size())
    swap(a, b);
  int ans = 1 << 30;
  if (s[0] != t[0]) {
    if (a.size() == b.size()) a.insert(a.begin(), 0);
    else b.insert(b.begin(), 0);
  }
  int x = a.size() - b.size();
  for (int rep = 0; rep < x; ++rep) b.push_back(n);
  ans = dist(a, b);
  for (int rep = 2; rep <= x; rep += 2) {
    b.pop_back(); b.pop_back();
    b.insert(b.begin(), 0); b.insert(b.begin(), 0);
    ans = min(ans, dist(a, b));
  }
  cout << ans;

  return 0;
}

#include<iostream>
using namespace std;
const int N = 5010;
int n;
int a[N] , b[N * 3];
char s[N] , t[N];
inline int Abs(int x) { return x < 0 ? -x : x; }
int calc()
{
	int tota = 0 , totb = 0;
	for(int i = 1 ; i < n ; ++i) if(s[i] == '0' && s[i + 1] == '1') a[++tota] = i;
	for(int i = 1 ; i <= n ; ++i) b[++totb] = 0;
	for(int i = 1 ; i < n ; ++i) if(t[i] == '0' && t[i + 1] == '1') b[++totb] = i;
	for(int i = 1 ; i <= n ; ++i) b[++totb] = n;
	int ans = 1e8;
	for(int i = 1 ; i <= totb - tota + 1 ; ++i)
	{
		int res = 0;
		for(int j = 1 ; j < i ; ++j) res += b[j];
		for(int j = 1 ; j <= tota ; ++j) res += Abs(a[j] - b[j + i - 1]);
		for(int j = tota + i ; j <= totb ; ++j) res += n - b[j];
		ans = min(ans , res); 
	}
	return ans;
}

int main()
{
	cin >> n >> (s + 1) >> (t + 1);
	if(n == 1) { cout << (s[1] != t[1]) << '\n'; return 0; }
	if(n == 2) { cout << ((s[1] != t[1]) && (s[2] != t[2])) << '\n'; return 0; }
	int ans = calc();
	for(int i = 1 ; i <= n ; ++i) // 算完红的算蓝的
		s[i] = '1' + '0' - s[i] , t[i] = '1' + '0' - t[i];
	cout << ans + calc() << '\n';
	return 0;
}
