#include <vector>
template <typename T> constexpr T my_abs(const T &x) { return x < 0 ? -x : x; }
char s[5005], t[5005];
int main()
{
	// freopen("AGC030-E.in", "r", stdin);
	int n;
	scanf("%d%s%s", &n, s, t);
	auto init = [&] (char *str)
	{
		std::vector<int> res;
		for (int i = 1; i < n; i++)
		{
			if (str[i] != str[i - 1])
				res.push_back(i);
		}
		return res;
	};
	auto vec_s = init(s), vec_t = init(t);
	auto get = [&] (std::vector<int> &vec, int idx)
	{
		if (idx < 0)
			return 0;
		if (idx >= vec.size())
			return n;
		return vec[idx];
	};
	int ans = 1e9, a = vec_s.size(), b = vec_t.size();
	for (int off = -a - 1; off <= b + 1; off++)
	{
		if ((off & 1) == (s[0] == t[0]))
			continue;
		int res = 0;
		for (int i = std::min(-off, 0); i < std::max(a, b - off); i++)
			res += my_abs(get(vec_s, i) - get(vec_t, i + off));
		ans = std::min(ans, res);
	}
	printf("%d\n", ans);
	return 0;
}

