inline void doit() {
    for (int add0 = 0; add0 <= 2 * n + 1; add0++) {
        // if ((add0 + (a.front() == b.front())) % 2 == 0)
        //     continue;
        vector <p> na;
        if (add0 <= n) {
            na = vector <p> (add0, p(0, 1));
            for (p v : a) na.pb(v);
        } else {
            na = vector <p> (add0 - n - 1, p(0, 1));
